package com;
import java.util.Scanner;
public class Ejercicio7_EDT {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//		7. El director de una escuela está organizando un viaje de estudios y requiere determinar cuánto 
//		debe cobrar a cada alumno y cuánto debe pagar a la compañía de viajes por el servicio. La forma de 
//		cobrar es la siguiente:
//		* Si son 100 alumnos o más, el costo por cada alumno es de 65 euros.
//		* De 50 a 99 alumnos, el costo es de 70 euros.
//		* De 30 a 49 alumnos, el costo es de 95 euros.
//		* Menos de 30 alumnos, el costo de la renta del autobús es de 4000 euros, sin importar el número de 
//		alumnos.
//		Realiza un algoritmo que permita determinar el pago a la compañía de autobuses y lo que debe 
//		pagar cada alumno por el viaje.

		Scanner my_scanner = new Scanner(System.in);
		System.out.println("¿Cuántos alumnos van a ir?");
		int alumnos = my_scanner.nextInt();
		
		if (alumnos >= 100) {
			System.out.println("Por persona serán 65 euros");
			System.out.println("Con un total de: " + alumnos*65 + " euros");
		} else if (alumnos > 49 && alumnos < 100) {
			System.out.println("Por persona serán 70 euros" );
			System.out.println("Con un total de: " + alumnos*70 + " euros");
		} else if (alumnos > 29 && alumnos < 49) {
			System.out.println("Por persona serán 95 euros");
			System.out.println("Con un total de: " + alumnos*95 + " euros");
		} else if ( alumnos < 30) {
			System.out.println("Serán $4,000 eruos sin importar cuántos alumnos sean.");
		}
		
		
		
	}

}
